document.addEventListener('DOMContentLoaded', function() {    initMobileMenu();
    initFloatingButtons();
});
function initMobileMenu() {    const mobileMenuToggle = document.getElementById('mobileMenuToggle');
    const mobileNav = document.getElementById('mobileNav');
    if (mobileMenuToggle && mobileNav) {        mobileMenuToggle.addEventListener('click', function() {            mobileNav.classList.toggle('active');
            mobileMenuToggle.classList.toggle('active');
        });
    }}function initFloatingButtons() {}document.querySelectorAll('a[href^="#"]').forEach(anchor => {    anchor.addEventListener('click', function (e) {        e.preventDefault();
        const target = document.querySelector(this.getAttribute('href'));
        if (target) {            target.scrollIntoView({                behavior: 'smooth',                block: 'start'            });
        }    });
});
